mapper.select:
select 

from mapping.SYS_DICT_INFO
where DICT_CODE = #{dictCode,jdbcType=VARCHAR}
  and DICT_VALUE = #{dictValue,jdbcType=VARCHAR}

mapper.delete:
delete from dbo.SYS_DICT_INFO
where DICT_CODE = #{dictCode,jdbcType=VARCHAR}
  and DICT_VALUE = #{dictValue,jdbcType=VARCHAR}

mapper.update:
update mapping.SYS_DICT_INFO


    DICT_LABEL = #{dictLabel,jdbcType=VARCHAR},


    DICT_SORT = #{dictSort,jdbcType=VARCHAR},


    DICT_DESC = #{dictDesc,jdbcType=VARCHAR},


where DICT_CODE = #{dictCode,jdbcType=VARCHAR}
  and DICT_VALUE = #{dictValue,jdbcType=VARCHAR}

update dbo.SYS_DICT_INFO
set DICT_LABEL = #{dictLabel,jdbcType=VARCHAR},
  DICT_SORT = #{dictSort,jdbcType=VARCHAR},
  DICT_DESC = #{dictDesc,jdbcType=VARCHAR}
where DICT_CODE = #{dictCode,jdbcType=VARCHAR}
  and DICT_VALUE = #{dictValue,jdbcType=VARCHAR}
